import 'dotenv/config'; // dotenv import (ìµœìƒë‹¨)
import fetch from 'node-fetch';
import { Network, Alchemy } from "alchemy-sdk";

// í™˜ê²½ ë³€ìˆ˜ì—ì„œ API í‚¤ ë° RPC URL ì„¸íŒ…
const ALCHEMY_API_KEY = process.env.ALCHEMY_API_KEY;
if (!ALCHEMY_API_KEY) {
    throw new Error("ALCHEMY_API_KEYê°€ .envì— ì •ì˜ë˜ì–´ ìžˆì§€ ì•ŠìŠµë‹ˆë‹¤.");
}
const ALCHEMY_RPC_URL = `https://eth-sepolia.g.alchemy.com/v2/${ALCHEMY_API_KEY}`;

// Alchemy ì„¸íŒ…
const config = {
    apiKey: ALCHEMY_API_KEY,
    network: Network.ETH_SEPOLIA,
};
const alchemy = new Alchemy(config);

// BigNumber to hex converter
function toHex(bn) {
    if (!bn) return undefined;
    if (typeof bn === 'string') return bn;
    if (bn._isBigNumber) return bn._hex;
    return bn;
}

// 1ï¸âƒ£ tx hash ê¸°ë°˜ params ìƒì„±
async function buildParamsFromTxHash(txHash) {
    const tx = await alchemy.core.getTransaction(txHash);
    if (!tx) throw new Error(`Transaction not found: ${txHash}`);

    return [
        {
            from: tx.from,
            to: tx.to,
            value: toHex(tx.value),
            data: tx.data
        },
        "latest"
    ];
}

// 2ï¸âƒ£ ì§ì ‘ ìž…ë ¥í•œ ê°’ìœ¼ë¡œ params êµ¬ì„±
function buildParamsFromInput(input) {
    return [
        {
            from: input.from,
            to: input.to,
            value: input.value,
            data: input.data
        },
        "latest"
    ];
}

// Access List ìš”ì²­ ì‹¤í–‰
async function sendAccessListRequest(params) {
    const payload = {
        jsonrpc: "2.0",
        method: "eth_createAccessList",
        params: params,
        id: 1
    };

    console.log("Sending payload:");
    console.log(JSON.stringify(payload, null, 2));

    const response = await fetch(ALCHEMY_RPC_URL, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
    });

    const result = await response.json();
    console.log("Access List response:");
    console.log(JSON.stringify(result, null, 2));
}

